import React, { useState } from "react";

function CommentInput() {
  const [comment, setComment] = useState('');
  console.log(comment);

  return (
    <div>
      <input
        value={comment}
        onChange={({ target }) => setComment(target.value)}
      />
      <span><strong>Post</strong></span>
    </div>
  );
};

export default CommentInput;
