import React from "react";

function Comment(props) {
  const { comment } = props;

  return (
    <div className="comment">
      <div className="infos">
        <span>
          <strong>{comment.author}</strong>
        </span><span>{comment.text}</span>
      </div>

    </div>
  );
};

export default Comment;
