import React from "react";

function Comment(props) {
  const { comment } = props;

  return (
    <div>
      <span>
        <strong>{comment.author}</strong>
      </span>
      <span>
        <p>{comment.text}</p>
      </span>
    </div>
  );
};

export default Comment;
