import React from 'react';
import Post from './Post';

function Posts() {
  const postsInitialInfos = [
    {
      author: 'meowed',
      image: 'gato-telefone',
      likesUser: 'respondeai',
      likesCounter: 101523,
      comments: [
        {
          author: 'smallcutecats',
          comment: 'Que fofo s2s2'
        },
        {
          author: 'bad.vibes.memes',
          comment: 'Sem graça'
        },
        {
          author: 'adorable_animals',
          comment: 'Lindinho'
        },
      ],
    },
    {
      author: 'barked',
      image: 'dog',
      likesUser: 'adorable_animals',
      likesCounter: 99159,
    },
    {
      author: 'chibirdart',
      image: 'dog',
      likesUser: 'smallcutecats',
      likesCounter: 85123,
    },
  ];

  const post = postsInitialInfos.map(({
    author,
    image,
    likesUser,
    likesCounter,
    comments,
  }) => {

    return (
      <Post
        key={author}
        author={author}
        image={image}
        likesUser={likesUser}
        likesCounter={likesCounter}
        comments={comments}
      />
    );
  });

  return (
    <div>
      <div className="posts">
        {post}
      </div>
    </div>
  );
}

export default Posts;
